import React, { useState } from 'react';
import MarkdownRenderer from './MarkdownRenderer';
import ChartRenderer from './ChartRenderer';
import DataTable from '../DataTable/DataTable';
import './ResponseDisplay.css';

const ResponseDisplay = ({ response }) => {
  const [activeView, setActiveView] = useState('summary');

  if (!response) {
    return (
      <div className="response-display-empty">
        <p className="text-gray-500">No response to display</p>
      </div>
    );
  }

  const { content, data, charts } = response;
  const hasData = data && data.rows && data.rows.length > 0;
  const hasCharts = charts && charts.length > 0;

  // Determine available tabs
  const tabs = [
    { id: 'summary', label: 'Summary', show: true },
    { id: 'data', label: 'Data', show: hasData },
    { id: 'charts', label: 'Visualizations', show: hasCharts },
  ].filter(tab => tab.show);

  return (
    <div className="response-display">
      {/* Tab Navigation */}
      {tabs.length > 1 && (
        <div className="flex space-x-1 mb-4 border-b border-gray-200">
          {tabs.map(tab => (
            <button
              key={tab.id}
              onClick={() => setActiveView(tab.id)}
              className={`px-4 py-2 text-sm font-medium transition-colors ${
                activeView === tab.id
                  ? 'text-blue-600 border-b-2 border-blue-600'
                  : 'text-gray-500 hover:text-gray-700'
              }`}
            >
              {tab.label}
              {tab.id === 'data' && data?.rowCount && (
                <span className="ml-2 px-2 py-0.5 text-xs bg-gray-100 rounded-full">
                  {data.rowCount}
                </span>
              )}
              {tab.id === 'charts' && charts?.length && (
                <span className="ml-2 px-2 py-0.5 text-xs bg-gray-100 rounded-full">
                  {charts.length}
                </span>
              )}
            </button>
          ))}
        </div>
      )}

      {/* Content Area */}
      <div className="response-content">
        {activeView === 'summary' && (
          <div className="prose prose-sm max-w-none">
            {content ? (
              <MarkdownRenderer content={content} />
            ) : (
              <p className="text-gray-500">No summary available</p>
            )}

            {/* Quick Stats */}
            {(hasData || hasCharts) && (
              <div className="mt-6 p-4 bg-blue-50 rounded-lg">
                <h4 className="text-sm font-semibold text-blue-900 mb-2">Query Results</h4>
                <div className="flex space-x-4 text-sm text-blue-700">
                  {hasData && (
                    <span>ðŸ“Š {data.rowCount} records found</span>
                  )}
                  {hasCharts && (
                    <span>ðŸ“ˆ {charts.length} visualization{charts.length > 1 ? 's' : ''} generated</span>
                  )}
                </div>
              </div>
            )}
          </div>
        )}

        {activeView === 'data' && hasData && (
          <div>
            <div className="mb-4">
              <h3 className="text-lg font-semibold text-gray-800 mb-2">
                Query Results
              </h3>
              <p className="text-sm text-gray-600">
                Found {data.rowCount} matching records
              </p>
            </div>
            <DataTable 
              data={data.rows} 
              maxRows={20}
            />

            {/* Export Options */}
            <div className="mt-4 flex space-x-2">
              <button
                onClick={() => downloadAsCSV(data.rows)}
                className="px-4 py-2 text-sm bg-gray-100 hover:bg-gray-200 rounded-lg transition-colors"
              >
                ðŸ“¥ Download CSV
              </button>
              <button
                onClick={() => copyToClipboard(data.rows)}
                className="px-4 py-2 text-sm bg-gray-100 hover:bg-gray-200 rounded-lg transition-colors"
              >
                ðŸ“‹ Copy to Clipboard
              </button>
            </div>
          </div>
        )}

        {activeView === 'charts' && hasCharts && (
          <div className="space-y-6">
            {charts.map((chart, index) => (
              <div key={index} className="chart-container-wrapper">
                <ChartRenderer 
                  chart={chart}
                  title={chart.title || `Chart ${index + 1}`}
                />
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

// Helper functions
const downloadAsCSV = (data) => {
  if (!data || data.length === 0) return;

  const headers = Object.keys(data[0]);
  const csvContent = [
    headers.join(','),
    ...data.map(row => 
      headers.map(header => {
        const value = row[header];
        // Escape commas and quotes in values
        if (typeof value === 'string' && (value.includes(',') || value.includes('"'))) {
          return `"${value.replace(/"/g, '""')}"`;
        }
        return value;
      }).join(',')
    )
  ].join('\n');

  const blob = new Blob([csvContent], { type: 'text/csv' });
  const url = window.URL.createObjectURL(blob);
  const a = document.createElement('a');
  a.href = url;
  a.download = `query_results_${new Date().toISOString()}.csv`;
  a.click();
  window.URL.revokeObjectURL(url);
};

const copyToClipboard = (data) => {
  if (!data || data.length === 0) return;

  const headers = Object.keys(data[0]);
  const tableText = [
    headers.join('\t'),
    ...data.map(row => 
      headers.map(header => row[header] || '').join('\t')
    )
  ].join('\n');

  navigator.clipboard.writeText(tableText).then(() => {
    // You could add a toast notification here
    console.log('Data copied to clipboard');
  });
};

export default ResponseDisplay;
