import type { Text } from '../shapes';
import { applyToText } from './text';

export function wrapIt(node: Text, wordWrapWidth: number, maxLines = 2, textBaseline: string = 'top') {
  applyToText(node, { wordWrap: true, wordWrapWidth, maxLines, textBaseline });
}
